// Home.js - React Component

import axios from "axios";
import { useEffect, useRef, useState } from "react";
import { FaArrowUp } from "react-icons/fa6";
import { ImEnlarge2 } from "react-icons/im";
import { IoMdClose } from "react-icons/io";
import iconImage from "../../images/icon.png";

import "../../App.css";
import Icon from "../../components/Chatbot-icon/icon";

const Home = () => {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState([
    { type: "bot", text: "Hi there! How can I assist you with information about MLRIT?" },
  ]);
  const [isOpen, setIsOpen] = useState(true); 
  const [isLoading, setIsLoading] = useState(false);
  const chatBodyRef = useRef(null); // Renamed ref for clarity


  useEffect(() => {
    if (chatBodyRef.current) {
      chatBodyRef.current.scrollTop = chatBodyRef.current.scrollHeight;
    }
  }, [messages]);


  const createMarkup = (htmlString) => {
    return { __html: htmlString };
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim() || isLoading) return;

    const userMsg = { type: "user", text: input };
    setMessages((prev) => [...prev, userMsg]);
    setIsLoading(true);
    setInput("");


    setTimeout(() => {
        setMessages((prev) => [...prev, { type: "bot", text: "Thinking...", isThinking: true }]);
    }, 300);


    try {
      const res = await axios.get(`http://localhost:5000/search?q=${encodeURIComponent(input)}`);


      setMessages((prev) => [...prev.slice(0, -1), { type: "bot", text: res.data.answer }]);

    } catch (error) {
      console.error("Error fetching response:", error);

      setMessages((prev) => [...prev.slice(0, -1), { type: "bot", text: "Oops! Something went wrong on my end. Please try again." }]);
    } finally {
        setIsLoading(false);
    }
  };

  return (
    <>
      {!isOpen && (
        <div className="chat-launcher" onClick={() => setIsOpen(true)}>
          <div className="small-minimize">
            <img src={iconImage} alt="Icon" className="minichat-icon" />
            <p className="launcher-text">Ask MLR</p>
          </div>
        </div>
      )}

      {isOpen && (
        <section className="chat-section">

          <div className="chat-container">
            <div className="chat-header">
              <div className="header-content">
                <div className="side1">
                  <Icon />
                  <h2 className="icon-text">Ask MLR</h2>
                </div>
                <div className="side2">
                  <div className="side-flex">
                    <div className="enlarge">
                      <ImEnlarge2 className="large-icon" />
                    </div>
                    <div className="close" onClick={() => setIsOpen(false)}>
                      <IoMdClose className="close-icon" />
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* The ref is now on the direct scrollable container */}
            <div className="chat-body" ref={chatBodyRef}>
              <div className="chat-body-content">
                {messages.map((msg, i) => (
                  <div
                    key={i}
                    className={`message ${
                      msg.type === "bot" ? "bot-message" : "user-message"
                    }`}
                  >
                    {msg.type === "bot" && !msg.isThinking && (
                      <img src={iconImage} alt="Icon" className="chat-icon" />
                    )}
                    <div
                      className={`message-text ${msg.isThinking ? 'thinking-bubble' : ''}`}
                      dangerouslySetInnerHTML={createMarkup(msg.text)}
                    />
                  </div>
                ))}
                {/* Visual indicator when loading */}
                {isLoading && messages[messages.length-1]?.isThinking && (
                    <div className="message bot-message">
                         <img src={iconImage} alt="Icon" className="chat-icon" />
                         <div className="message-text thinking-animation">
                            <span></span><span></span><span></span>
                         </div>
                    </div>
                )}
              </div>
            </div>

            <div className="chatbot-footer">
              <div className="footer-content">
                <form onSubmit={handleSubmit} className="chat-form">
                  <input
                    type="text"
                    placeholder="Ask MLR"
                    className="message-input"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    disabled={isLoading}
                    required
                  />
                  <button type="submit" disabled={isLoading}>
                    <FaArrowUp />
                  </button>
                </form>
              </div>
            </div>
          </div>
        </section>
      )}
    </>
  );
};

export default Home;
